class HomeMo {
  HomeMo({
    List<BannerMo>? bannerList,
    List<CategoryMo>? categoryList,
    List<VideoMo>? videoList,
  }) {
    _bannerList = bannerList;
    _categoryList = categoryList;
    _videoList = videoList;
  }

  HomeMo.fromJson(dynamic json) {
    if (json['bannerList'] != null) {
      _bannerList = [];
      json['bannerList'].forEach((v) {
        _bannerList?.add(BannerMo.fromJson(v));
      });
    }
    if (json['categoryList'] != null) {
      _categoryList = [];
      json['categoryList'].forEach((v) {
        _categoryList?.add(CategoryMo.fromJson(v));
      });
    }
    if (json['videoList'] != null) {
      _videoList = [];
      json['videoList'].forEach((v) {
        _videoList?.add(VideoMo.fromJson(v));
      });
    }
  }

  List<BannerMo>? _bannerList;
  List<CategoryMo>? _categoryList;
  List<VideoMo>? _videoList;

  List<BannerMo>? get bannerList => _bannerList;

  List<CategoryMo>? get categoryList => _categoryList;

  List<VideoMo>? get videoList => _videoList;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    if (_bannerList != null) {
      map['bannerList'] = _bannerList?.map((v) => v.toJson()).toList();
    }
    if (_categoryList != null) {
      map['categoryList'] = _categoryList?.map((v) => v.toJson()).toList();
    }
    if (_videoList != null) {
      map['videoList'] = _videoList?.map((v) => v.toJson()).toList();
    }
    return map;
  }
}
